import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'about',
  templateUrl: './about.component.html',
  styleUrls: ['./about.component.scss']
})
export class AboutComponent implements OnInit {

  constructor() { }
  title: string = "Hello, my name is Somesh ";
  paragraph: string = `Experienced Angular Developer with 2+ years of hands-on expertise, specializing in
  crafting scalable web applications. Proficient in Angular, JavaScript, HTML, CSS, and
  experienced in developing single-page applications (SPAs) using Angular.`;

  ngOnInit() {}

}
